//========================================================== Script liens NZB =========================================================


function make_link(elem,cible,title,img){
	var newLink = document.createElement("a");
	newLink.href=cible;
	newLink.target="_blank";
	var new_img = document.createElement("img");
	new_img.src=img;
	new_img.title="Chercher sur "+title;
	new_img.alt="Chercher sur "+title;
	newLink.appendChild(new_img);
	elem.appendChild(newLink);
}
function add_links(){
	var search_array = document.getElementsByTagName("TD");
	for (var n=0; n < search_array.length; ++n){
		var Td = search_array[n];
		if(Td.innerHTML.match(/flag/)){
						var z=1;
						do{
						++z;
					}while(search_array[n+z].innerHTML.match(/ng_id/));
				var newelem=search_array[n+z];
                var div = document.createElement("div");
                var cont=newelem.innerHTML;
				var req=cont.replace(/ /g,"+").split("*").join(" ").replace(/&lt;/,"<").replace(/&gt;/,">").replace(/#/,"%23");
                var method1=req.replace(/&amp;/,"");
            	var method2=encodeURIComponent(cont.replace(/&amp;/," "));
                var method3=encodeURIComponent(cont);
                 var method4=escape(cont.replace(/&amp;/,"%26"));
                  var method5=cont.replace(/ /g,"+");
                  var method6=cont.replace(/ /g,"+").replace(/_/g,"+");


                make_link(div,"nzblnk:?t="+method3+('&h=')+method3,"Nzb_Monkey","data:image/jpeg;base64,");
      
                make_link(div,"https://www.binsearch.info/?max=250&adv_age=&server=1&q="+method1,"Binsearch","data:image/png;base64,");
      
                make_link(div,"https://www.binsearch.info/?max=250&adv_age=&server=2&q="+method1,"Binsearch \"other groups\"","data:image/png;base64,");

                make_link(div,"https://www.nzbindex.nl/search/?q="+method1,"NZBindex.nl","data:image/x-png;base64,");

                make_link(div,"https://beta.nzbindex.com/?q="+method1,"BETA","data:image/x-png;base64,");
          
                make_link(div,"https://www.newzleech.com/?group=&minage=&age=&min=Size+%28Min%29&max=Size+%28Max%29&q="+method5 +('&m=search&adv='), "Newzleech.com","data:image/png;base64,");
          
                make_link(div,"http://www.nzbking.com/search/?q="+method6,"NZBKing","data:image/png;base64,");

                make_link(div,"http://binzb.com/search?q="+method5,"BiNZB","data:image/png;base64,"); 

                make_link(div,"http://nzbfriends.com/?q="+method6,"NzbFriends","data:image/jpeg;base64,");          
          
                make_link(div,"https://www.usenet-crawler.com/search?val="+method3 +('&age=-1&index=3'), "Usenet_crawler","data:image/png;base64,");
      
   
				div.style.display="none";
				newelem.addEventListener("mouseover",make_visible,false);
				newelem.appendChild(div);
		}
	}
}

 

function make_visible(e){
	var div=e.target.getElementsByTagName("div")[0];
	if(div){
		if(olddiv){
			olddiv.style.display="none";
		}
		olddiv=div;
		div.style.display="block";
	}
}

var olddiv;
add_links();
