        if (a_spr == - 1) a_dni2 = 0;
         else a_dni2 = a_flag.substring(0, a_spr);
        a_min2 = Number(a_flag.slice( - 5, - 3));
        a_godz2 = Number(a_flag.slice( - 8, - 6));
