// ==UserScript==
// @name         Darkwarez Menu Mobile 
// @version      1.1
// @author       Boltex & ThomassPWA
// @description  Dodaje menu do wersji mobilnej
// @namespace    dw.menu.mobile
// @match        http://darkwarez.pl/forum*
// @match        https://darkwarez.pl/forum*
// @require      http://code.jquery.com/jquery-1.11.1.min.js
// @grant        none
// ==/UserScript==

$(document).ready(function()
{
    var html = $('body').html();

    // SprawdÅº czy zalogowany
    if( html.indexOf("Zaloguj") > -1 ) throw new Error("menu tylko dla zalogowanych");

    $('body').wrapInner('<div class="content">');
    var menu = "",tmp,id,nick,sid;
    var diax = "data:image/svg+xml;base64,";
    var defaultImg = "https://svgshare.com/i/5CP.svg";


    menu += '<nav role="off-canvas">';
    menu += ' <ul>';
    menu += '  <li><img id="md6" class="avatar" alt="AVATAR" /></li> ';
    menu += '  <li><b id="md2">?</b>&nbsp;&nbsp;&nbsp;<img id="losInit" src="' + diax + '" border="0" style="display:none;"> <div id="losInfo" style="display:none; font-size:11px;"></div>';
    menu += '  <li><a href="search.php">SZUKAJ</a></li> ';
    menu += '  <li><a href="groupcp.php">GRUPY</a></li>  ';
    menu += '  <li><a href="staty.php">TOP 10</a></li> ';
    menu += '  <li><a href="profile.php?mode=editprofile">Profil</a></li> ';
    menu += '  <li><a href="oznaczone.php">OZNACZONE</a></li> ';
    menu += '  <li><a id="md4" href="privmsg.php?folder=inbox">WIADOMOÅCI [ ? ]</a></li>  ';
	menu += '  <li><a id="md7" href="#">POWIADOMIENIA [ ? ]</a></li> ';
	menu += '  <li><a id="md1" href="diamenty.php">DIAMENTY [ <span style="color: rgb(0, 153, 255);"><b>?</b></span> ]</a></li>  ';
	menu += '  <li><a id="md3" href="ranking.php?me">RANKING [ <b>?</b> ]</a></li>  ';
    menu += '  <li><a id="md5" href="#">WYLOGUJ</a></li>  ';
    menu += ' </ul> ';
    menu += '</nav> ';

    $(menu).prependTo('body');
    $('<div class="circleNot" style="display:none;"></div><div class="circlePM" style="display:none;"></div><label for="menu" onclick></label> <input type="checkbox" id="menu">').prependTo('body');
    $('</div><a href="#" onclick="window.scrollTo(0,0);" class="scrollup">Scroll</a>').appendTo('body');

    // Pobierz diamenty
    tmp = new RegExp('href="diamenty.php"> (.+?)<s').exec( html );
    tmp = tmp[1].split("<span");
    $("#md1").html('DIAMENTY [ <span style="color: rgb(0, 153, 255);"><b>' + tmp[0] + '</b></span> ]');

    // Pobierz ID + nick profilu
    tmp = new RegExp('<a href="profil-(.+?).html" class="mainmenu"').exec( html ); id = tmp[1];
    tmp = new RegExp('Wyloguj \\[ (.+?) \\]').exec( html ); nick = tmp[1];
    $("#md2").attr("onclick","document.location.href='./profil-"+id+".html';");
    $("#md2").text( nick.toUpperCase() );

    // Pobierz pozycje z rankingu
    tmp = new RegExp('<font color="#a4a4a4">(.+?)</font>').exec( html );
    $("#md3").text( "RANKING [ "+tmp[1]+" ]" );

    // Pobierz powiadomienia
    if( new RegExp('class="mainmenu">Powiadom').test( html ) )
    {
        tmp = new RegExp('<span style="color:#00ee00">\\[ (.+?) \\]</span>').exec( html );
        $("#md7").html( 'POWIADOMIENIA [ <span style="color: rgb(46,170,28);"><b>' +tmp[1]+ '</b></span> ]' );

        tmp = new RegExp('href="powiadom.+sid=(.+?)&amp;mobile=1"').exec( html );
        $("#md7").attr("href", "powiadom.php?uid=" + id + "&sid=" + tmp[1] + "&mobile=1");
        $('.circleNot').show();
    }
    else
    {
        $("#md7").text( "POWIADOMIENIA [ 0 ]" );
    }

    // Pobierz wiadomoÅci
    if( new RegExp('title="warez">WiadomoÅci</a>').test( html ) )
    {
        $("#md4").text( "WIADOMOÅCI [ 0 ]" );
    }
    else
    {
        tmp = new RegExp('WiadomoÅci \\[ (.+?) \\]').exec( html );
        $("#md4").html( "WIADOMOÅCI [ <span style='font-weight:bold; color:#ee0000;'>" +tmp[1]+ "</span> ]" );
        $('.circlePM').show();
    }

    // Pobierz SID do logout.php
    tmp = new RegExp('logout=true.+sid=(.+?)"').exec( html );
    sid = tmp[1];

    // WyczyÅÄ cookies podczas wylogowania
    $("#md5").click(function()
    {
        setCookie("avatar","",0);
        setCookie("color","",0);
        window.location.href = "./login.php?logout=true&sid=" + sid;
    });

    // SprawdÅº czy jest losowanie
    if( html.indexOf("losujd('');") < 0 )
    {
        $("#losInit").show();
    }

    // Losowanie
    $("#losInit").click(function()
    {
        $("#losInit").attr("id","losOff");

        // Pobierz token
        tmp = new RegExp('"losujd(.+?)"').exec( html );
        tmp = tmp[1].split("'");
        tmp = tmp[1];

        // WyÅlij zapytanie
         $.get( "./losujd.php?id=" + tmp, function( data )
         {
             data = data.split('innerHTML = "');
             data = data[1];

             if( data.indexOf("+") > -1 )
             {
                 data = data.split("<img");
                 data = data[0].trim();

                 $("#losInfo").html( data );
                 $("#losInfo").show();
             }
             else
             {
                 data = data.split('";');
                 data = data[0].trim();
                 alert( data );
             }
         });
    });

    // Pobierz avatar + kolor rangi
    if( typeof getCookie("avatar") !== "undefined" && getCookie("avatar") != "" && typeof getCookie("color") !== "undefined" && getCookie("color") != "")
    {
        $("#md6").attr('src', getCookie("avatar"));
        $("#md2").css('color', getCookie("color"));
    }
    else
    {
        $.get( "./profil-"+id+".html", function( data )
        {
            // Pobierz avatar
            if( new RegExp('<img onload="rmwa_img_loaded.+" src="(.+?)"').test( data ) )
            {
                tmp = new RegExp('<img onload="rmwa_img_loaded.+" src="(.+?)"').exec( data );
                if( tmp[1].indexOf("imgur.com/") > -1 ) tmp[1] = "http://images.weserv.nl/?url=" + tmp[1].replace("https://").replace("http://").trim();
                if( tmp[1].indexOf("avcenz.gif") > -1 ) tmp[1] = defaultImg;
                setCookie("avatar", tmp[1], 1);
                $("#md6").attr('src', tmp[1]);
            }
            else
            {
                setCookie("avatar", defaultImg, 1);
                $("#md6").attr('src', defaultImg);
            }

            // Pobierz kolor rangi
            tmp = new RegExp('Kontakt z (.+?)</span>').exec( data );
            if( tmp[1].indexOf("#") > -1 )
            {
                tmp = tmp[1].split("#");
                tmp = "#" + tmp[1].substring(0,6).toUpperCase().trim() + ";";
                setCookie("color", tmp, 1);
            }
            else
            {
                setCookie("color", "#FFB300;", 1);
            }
            $("#md2").css('color', getCookie("color"));
        });
    }

    function setCookie(cname, cvalue, exdays)
    {
        var d = new Date();
        d.setTime(d.getTime() + (exdays*24*60*60*1000));
        var expires = "expires="+ d.toUTCString();
        document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
    }

    function getCookie(cname)
    {
        var name = cname + "=";
        var decodedCookie = decodeURIComponent(document.cookie);
        var ca = decodedCookie.split(';');
        for(var i = 0; i <ca.length; i++) {
            var c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }
});

$(document).ready(function () {

    $(window).scroll(function () {
        if ($(this).scrollTop() > 100) {
            $('.scrollup').fadeIn();
        } else {
            $('.scrollup').fadeOut();
        }
    });

    $('.scrollup').click(function () {
        $("html, body").animate({
            scrollTop: 0
        }, 600);
        return false;
    });

});


