// ==UserScript==
// @name           CT_beeponmail
// @namespace      CheckT
// @author         CheckT
// @description    Сигнал по приходу почты; работает только на странице почты
// @version        1.2.7
// @encoding       utf-8
// @homepage       https://greasyfork.org/en/scripts/374697-ct-beeponmail
// @include        https://www.heroeswm.ru/sms.php*
// @include        https://www.lordswm.com/sms.php*
// @include        http://178.248.235.15/sms.php*
// @include        https://www.heroeswm.ru/sms_clans.php*
// @include        https://www.lordswm.com/sms_clans.php*
// @include        http://178.248.235.15/sms_clans.php*
// ==/UserScript==

// (c) 2018  CheckT
// firefox: enable
//  media.mediasource.enabled
//      см. также https://support.mozilla.org/en-US/questions/1061489

(function() {

  var delay = 60;       //секунд между запросами проверки почты
  var repeat = 4;       //повторов звукового сигнала
  var is_reload = true; //false, чтобы не перегружать страницу после сигнала: тогда сигнал будет повторяться
  var audio = '';       //напишите сюда адрес звука для сигнала, иначе будет звук по умолчанию
                        //например:
                        //   var audio = 'https://www.soundjay.com/button/button-2.mp3';

  var url_req = location.protocol+'//'+location.hostname+'/pl_info.php?id=1';
      //страница, на которой проверяется значок почты

  var beep_snd = init_audio();
  request();
  return;

  function request(){
    var objXMLHttpReq = createXMLHttpReq();
    try{
      objXMLHttpReq.open('GET', url_req, true);
      setXMLHttpReqHeaders(objXMLHttpReq);
      objXMLHttpReq.onreadystatechange = function() { checkMail(objXMLHttpReq); }
      objXMLHttpReq.send(null);
    }catch(e){alert("CT_beeponmail: "+e);}
    setTimeout(request, delay*1000);
  }

  function checkMail(objXMLHttpReq){
    if(objXMLHttpReq.responseText && objXMLHttpReq.responseText.indexOf('pismo.gif') > -1) {
      beep_n(repeat);
    }
  }

  function beep_n(n){
    if(n>0){
      beep_snd.play();
      setTimeout(beep_n,1200,n-1);
    } else if (is_reload){
      location.reload();
    }
  }

  function init_audio(){
    if(audio && audio != ''){
      var snd = new Audio();
      snd.src = audio;
      snd.preload = true;
      return snd;
    } else {
      return new Audio(
        "data:audio/mp3;base64,"
      );
    }

  }

  function createXMLHttpReq(){
    var objXMLHttpReq;

    if (window.XMLHttpRequest){
      objXMLHttpReq = new XMLHttpRequest();
    } else if (window.ActiveXObject) {
      // IE
      objXMLHttpReq = new ActiveXObject("Microsoft.XMLHTTP");
    } else {
      alert('Can\'t create XMLHttpRequest!');
    }
    return objXMLHttpReq;
  }

  function setXMLHttpReqHeaders(objXMLHttpReq){
    //objXMLHttpReq.setRequestHeader('Referer', 'https://www.heroeswm.ru/inventory.php');
    objXMLHttpReq.setRequestHeader('Content-type', 'text/html; charset=windows-1251');
    if(objXMLHttpReq.overrideMimeType)
      objXMLHttpReq.overrideMimeType('text/html; charset=windows-1251');
  }
})();
