// ==UserScript==
// @name         Show My Pitchfork Score & Search in Spotify
// @namespace    http://your.homepage/
// @version      0.1
// @description  Get the score of each album on the overview of the reviews & get a link to search album on Spotify
// @author       Cisco
// @match        http://pitchfork.com/*
// @require http://code.jquery.com/jquery-latest.js
// @grant        none
// ==/UserScript==

var scoreDiv= document.createElement('div');
scoreDiv.id='score';

$('body').append(scoreDiv);
$('#score').hide();

function addSAS() {

var recall= setInterval(function(){

    if($('a[href^="/reviews"] > .artwork').length >= 20) {
        
        clearInterval(recall);
        
        $('a[href^="/reviews"] > .artwork').each(function(){
            
            var artwork= $(this);
            
            var targetURL= artwork.parent().attr('href');
            
            $('#score').load(targetURL + ' .score', function() {
                
                var scoreThis= $('#score > span').text();
                
                var artist= $.trim(artwork.next().children('h1').text().replace(/[\s\']/g, '+'));
                
                var album= $.trim(artwork.next().children('h2').text().replace(/[\s\']/g, '+'));
                
                artwork.append('<span class="scrapped-score">' + scoreThis + '</span>');
                
                if(artwork.next().next('.p4ktag').length > 0) {
                    artwork.children('.scrapped-score').addClass('bnm');
                }
                
                artwork.append('<a class="spotify-link" href="spotify:search:' + artist + '+' + album + '">&nbsp;</a>');
                
                artwork.unwrap();
                
                artwork.next().wrap('<a href="' + targetURL + '"></a>');
                
            }); // End of LOAD
            
        }); // End of EACH
        
    }
    
}, 1000);

} // End of Function addSAS()


function checkURLChange(URL) {

    if(URL !== document.location.href) {
        
        currURL= document.location.href;
        
        if(URL.match(/reviews\/albums/)) {
            
            addSAS();
            
        }
    }
    
} // End of function CheckURLChange()

// INIT
if(document.location.href.match(/reviews\/albums/)) {
    addSAS();
}
var currURL= document.location.href;
setInterval(function() {checkURLChange(currURL);}, 1000);

var cssRules= '<style>'

+ '.scrapped-score {'
+ 'line-height: 1;'
+ 'background-color: #fff;'
+ 'padding: 5px;'
+ 'position: absolute;'
+ 'font-size: 16px;'
+ 'top: 3px;'
+ 'left: 3px;'
+ 'border-radius: 4px;'
+ 'border: 2px solid;'
+ 'font-weight: bold;'
+ 'opacity: 0.9;'
+ '}'

+ '.most-read .scrapped-score {'
+ 'left: 21px;'
+ '}'

+ '.spotify-link {'
+ 'background-image: url(data:image/svg+xml;base64,);'
+ 'background-repeat: no-repeat;'
+ 'background-position: right top;'
+ 'background-size: 80px;'
+ 'padding: 0 0 20px 20px;'
+ 'width: 80px;'
+ 'height: 30px;'
+ 'position: absolute;'
+ 'top: 3px;'
+ 'right: 3px;'
+ 'z-index: 91;'
+ 'opacity: 0.9;'
+ 'transition: 200ms all'
+ '}'

+ '.spotify-link:hover {'
+ 'opacity: 1;'
+ '.spotify-link }'

+ '.most-read .spotify-link {'
+ 'right: 21px;'
+ '}'

+ '.bnm {'
+ 'color: #ef4135;'
+ '}'


+ '</style>';

$('head').append(cssRules);