(async function() {
    'use strict';
    const DBG = false;
    const PAGE = window.location.pathname
    const RegEx = /(index.php)/g
    const URL = PAGE.replace(RegEx,'')
    const SCRIPTNAME = GM_info.script.name;
    const data = await fetch('https://spreadsheets.google.com/feeds/list/1YwBQ7KoJrAtOrq0NsQY-NGJCQfZLdVi32CMs6WSgo-w/1/public/values?alt=json').then(response => response.json());
    const BlackList = data.feed.entry.map(entry => entry.gsx$username.$t.toLowerCase());
    const arrBirthdayList = [];
    $('div#page-body p').eq(3).find('strong a').each(function() { if (!BlackList.includes($(this).text().toLowerCase())) arrBirthdayList.push($(this).text())});
    const arrBirthdayListDiscord = [];
    $('div#page-body p').eq(3).find('strong a').each( function(i){ arrBirthdayListDiscord[i] = $(this).text(); });
    const DIMG = 'data:image/png;base64,'
    const DIMGP = 'data:image/png;base64,'
    const PMIMG = 'data:image/png;base64,'
    const PMIMGP = 'data:image/png;base64,'
    //const today = new Date().toLocaleDateString('en-us', { month: 'long', day: 'numeric', year: 'numeric' });
    const todayRegEx = /(It is currently )|\s\d+:\d+\s(pm|am)/g
    const optoutnotice = encodeURIComponent('\n[size=85][color=#BF0000]** Should you wish to [b]OPT-OUT[/b] of any future birthday messages, please fill out [url=http://bit.ly/2SCslMA]this form[/url].[/color][/size]')
    const MYNAME = $('#phpbb > div.waze-header > div > div > div.login_tab > span')[0].textContent.replace('hello ','').replace('  ','');
    function custMessage() {
        if (DBG == false) {
            let today = localStorage.getItem('WMEBtoday');
            const CUSTMSG = localStorage.getItem('CSTMSG');
            const CUSTPMLink = URL + 'ucp.php?i=pm&mode=compose&subject=Happy Birthday ('+ today +')&username_list='+ MYNAME +'&message=' + CUSTMSG + optoutnotice + '&bcc_list=';
            let copyText = arrBirthdayList.join('\n');
            console.info('Birthdays for PM: \n\n'+copyText);
            let PMList1 = CUSTPMLink + arrBirthdayList.slice(0, 20).join('%0A%0D')
            window.open(PMList1);
            if(arrBirthdayList.length > 20){
                let PMList2 = CUSTPMLink + arrBirthdayList.slice(20, 40).join('%0A%0D')
                window.open(PMList2);
                if (arrBirthdayList.length > 40){
                    let PMList3 = CUSTPMLink + arrBirthdayList.slice(40, 60).join('%0A%0D');
                    window.open(PMList3);
                    if (arrBirthdayList.length > 60){
                        let PMList4 = CUSTPMLink + arrBirthdayList.slice(60, 80).join('%0A%0D');
                        window.open(PMList4);
                    };
                };
            };
        };
        if (DBG == true) {
            console.log('List Length: ' + arrBirthdayList.length + '.\n' + 'The following lists have been triggered' + '\n\n' + 'PMList1 (CST)');
            if (arrBirthdayList.length > 20) {
                console.log('PMList2 (CST)');
                if (arrBirthdayList.length > 40) {
                    console.log('PMList3 (CST)');
                    if (arrBirthdayList.length > 60) {
                        console.log('PMList4 (CST)')
                    };
                };
            };
        };
    };
    function createMessage() {
        if (DBG == false) {
            let today = localStorage.getItem('WMEBtoday');
            const PDMESSAGE = encodeURIComponent([
                'Good day all,',' ',
                'On behalf of all of the editing community, I just want to stop by and wish you all a very [b]Happy Birthday[/b] and hope its as relaxing and enjoyable as possible. Thank you for all the hard work you do to make Waze such an amazing platform.',' ', '- '+MYNAME,' ',' ',
                '[color=#FF0000][size=75]Note: If today is [b]NOT[/b] your birthday, please check your forum settings by [url=https://www.waze.com/forum/ucp.php?i=164]clicking here[/url] to verify it is entered correctly.[/size][/color]','','',
                '[size=85]In case you are not aware, editors can use Discord to communicate with the editing community worldwide. Here is a list of servers available for you to join:',' ',
                '[b]Global (ROW)* & MapRaid[/b] - [url]https://discord.gg/YEtgvCN[/url]',
                '[b]Waze USA[/b] - [url]https://discord.gg/Q3YPQC6[/url]',
                /*'[b]Great Lakes Region (GLR)[/b] - [url]https://discord.me/wazeglr[/url]',
                '[b]Mid Atlantic Region (MAR)[/b] - [url]https://goo.gl/E5CKDk[/url]',
                '[b]New England Region (NER)[/b] - [url]https://goo.gl/forms/X24ENSZ6ANw2sQpV2[/url]',
                '[b]Northeast Region (NOR)[/b] - [url]https://discord.me/NorthEast_Region[/url]',
                '[b]Northwest Region (NWR)[/b] - [url]https://discord.me/wazenwr[/url]',
                '[b]Plains Region (PLN)[/b] - [url]http://www.discord.me/wazepln[/url]',
                '[b]South Atlantic Region (SAT)[/b] - [url]https://goo.gl/yjKKgv[/url]',
                '[b]South Central Region (SCR)[/b] - [url]https://discord.me/waze_scr[/url]',
                '[b]Southeast Region (SER)[/b] - [url]https://discord.me/southeast[/url]',
                '[b]Southwest Region (SWR)[/b] - [url]https://discord.gg/twxnqnU[/url]',
                '[b]Territories (ATR)[/b] - [url]https://discord.gg/Vup2VEA[/url]',*/
                '[b]Australia[/b] - [url]https://discord.me/wazeaustralia[/url]',
                '[b]Scripts[/b] - [url]https://discord.me/WazeScripts[/url][/size]',
                //'[b]VEOC[/b] - [url]https://discord.me/waze_veoc[/url][/size]',
                '[size=70]* Note your country may not utilize Discord, please check with your local community.',
                '[color=#BF0000]** Should you wish to [b]OPT-OUT[/b] of any future birthday messages, please fill out [url=http://bit.ly/2SCslMA]this form[/url].[/color][/size]',
            ].join('\n'));
            const PMLink = URL + 'ucp.php?i=pm&mode=compose&subject=Happy Birthday ('+ today +')&username_list='+ MYNAME +'&message='+ PDMESSAGE +'&bcc_list=';
            let copyText = arrBirthdayList.join( '\n' );
            console.info('Birthdays for PM: \n\n'+copyText);
            let PMList1 = PMLink + arrBirthdayList.slice(0, 20).join('%0A%0D');
            window.open(PMList1);
            if(arrBirthdayList.length > 20){
                let PMList2 = PMLink + arrBirthdayList.slice(20, 40).join('%0A%0D');
                window.open(PMList2);
                if (arrBirthdayList.length > 40){
                    let PMList3 = PMLink + arrBirthdayList.slice(40, 60).join('%0A%0D');
                    window.open(PMList3);
                    if (arrBirthdayList.length > 60) {
                        let PMList4 = PMLink + arrBirthdayList.slice(60,80).join('%0A%0D');
                    };
                };
            };
        };
        if (DBG == true) {
            console.log('List Length: ' + arrBirthdayList.length + '.\n' + 'The following lists have been triggered' + '\n\n' + 'PMList1 (PDM)');
            if (arrBirthdayList.length > 20) {
                console.log('PMList2 (PDM)');
                if (arrBirthdayList.length > 40) {
                    console.log('PMList3 (PDM)');
                    if (arrBirthdayList.length > 60) {
                        console.log('PMList4 (PDM)');
                    };
                };
            };
        };
    };
    function createBMessage() {
        if (DBG == false) {
            let today = localStorage.getItem('WMEBtoday');
            const BPMBlink = URL + 'ucp.php?i=pm&mode=compose&subject=Happy Birthday ('+ today +')&username_list='+ MYNAME +'&message=' + optoutnotice + '&bcc_list=';
            let copyText = arrBirthdayList.join( '\n' );
            console.info('Birthdays for PM: \n\n'+copyText);
            let PMList1 = BPMBlink + arrBirthdayList.slice(0, 20).join('%0A%0D');
            window.open(PMList1);
            if(arrBirthdayList.length > 20){
                let PMList2 = BPMBlink + arrBirthdayList.slice(20, 40).join('%0A%0D');
                window.open(PMList2);
                if (arrBirthdayList.length > 40){
                    let PMList3 = BPMBlink + arrBirthdayList.slice(40, 60).join('%0A%0D');
                    window.open(PMList3);
                    if (arrBirthdayList.length > 60){
                        let PMList4 = BPMBlink + arrBirthdayList.slice(60, 80).join('%0A%0D');
                        window.open(PMList4);
                    };
                };
            };
        };
        if (DBG == true) {
            console.log('List Length: ' + arrBirthdayList.length + '.\n' + 'The following lists have been triggered' + '\n\n' + 'PMList1 (BPM)');
            if (arrBirthdayList.length > 20) {
                console.log('PMList2 (BPM)');
                if (arrBirthdayList.length > 40) {
                    console.log('PMList3 (BPM)');
                    if (arrBirthdayList.length > 60) {
                        console.log('PMList4 (BPM)');
                    };
                };
            };
        };
    };
    const link = document.createElement('a');
    link.href = "#";
    const a = link;

    function createTemplate() {
        const sigbox = $('#attach_sig')[0];
        const tdiv = document.createElement('div');
        tdiv.id = 'TBOX';
        $('#message').after(tdiv);
        var tbox = document.createElement("INPUT");
        tbox.setAttribute("type", "checkbox");
        tbox.id = 'TMSG'
        const tl = document.createElement("LABEL");
        if (DBG == true) {
            tl.innerHTML = ' Save Template (Debug)';
        } else {
            tl.innerHTML = '  Save Template';
        };
        tdiv.after(tbox);
        tbox.after(tl);
        if (DBG == true) {
            tbox.onclick = function() {
                if ($('#message')[0].value == '') {
                    console.log('Save Template Box CHECKED\n\nTemplate successfully deleted. (Debug Only)');
                    $('#TMSG').attr()[0].checked = false;
                };
                if ($('#message')[0].value !== "") {
                    var template = encodeURIComponent($('#message')[0].value);
                    console.log('Save Template Box CHECKED\n\nTemplate successfully saved. (Debug Only): \n\n' + 'Plain Text:\n' + $('#message')[0].value +'\n\nEncoded Text:\n' + template);
                    $('#TMSG').attr()[0].checked = false;
                };
            };
        } else {
            tbox.onclick = function() {
                if ($('#message')[0].value == '') {
                    window.alert('Template successfully deleted. You may now close this PM window.');
                    localStorage.setItem('CSTMSG', '');
                    $('#TMSG').attr()[0].checked = false;
                    localStorage.setItem('CMSG', false);
                };
                if ($('#message')[0].value !== "") {
                    var template = encodeURIComponent($('#message')[0].value);
                    localStorage.setItem('CSTMSG', template);
                    window.alert('Template successfully saved. You may now close this PM window.');
                    $('#TMSG').attr()[0].checked = false;
                };
            };
        };
        if (DBG == true) {
            sigbox.onclick = function() {
                if (sigbox.checked) {
                    console.log('Attach Signature Box CHECKED');
                };
                if (!sigbox.checked) {
                    console.log('Attach Signature Box UNCHECKED');
                };
            };
        } else {
            sigbox.onclick = function() {
                if (sigbox.checked) {
                    localStorage.setItem('SIG', 'true');
                };
                if (!sigbox.checked) {
                    localStorage.setItem('SIG', 'false');
                };
            };
        };
    };
    function BirthdayButton() {
        const pdiv = document.createElement('div');
        pdiv.id = 'P-IMG';
        $('#wrap').after(pdiv);
        const box = document.createElement("INPUT");
        box.setAttribute("type", "checkbox");
        box.id = 'PDM'
        const cl = document.createElement("LABEL");
        if (DBG == true) {
            cl.innerHTML = ' Use Pre-Defined PM Message (Debug)';
        } else {
            cl.innerHTML = '  Use Pre-Defined PM Message (Beta)';
        }
        pdiv.after(box);
        box.after(cl);
        if (DBG == true) {
            box.onclick = function() {
                if ($('#PDM').attr()[0].checked == true) {
                    $('#CMSG').attr()[0].checked = false
                    console.log('Pre-Defined Message Box CHECEKD');
                };
                if ($('#PDM').attr()[0].checked == false) {
                    console.log('Pre-Defined Message Box UNCHECEKD');
                };
            };
        } else {
            box.onclick = function() {
                if (localStorage.getItem('CMSG') == 'true') {
                    $('#CMSG').attr()[0].checked = false;
                    localStorage.setItem('CMSG', 'false');
                };
                localStorage.setItem('PDM', box.checked);
            };
        };
        const box1 = document.createElement("INPUT");
        box1.setAttribute("type", "checkbox");
        box1.id = 'CMSG'
        const cl1 = document.createElement("LABEL");
        cl1.innerHTML = '  Use Custom PM Message  |  ';
        pdiv.after(box1);
        box1.after(cl1)
        if (DBG == true) {
            box1.onclick = function() {
                if (localStorage.getItem('PDM') == true) {
                    $('#PDM').attr()[0].checked = false;
                    console.log('Custom Message Box CHECEKD');
                };
                if (localStorage.getItem('PDM') == false) {
                    console.log('Custom Message Box UNCHECEKD');
                };
            };
        } else {
            box1.onclick = function() {
                if (localStorage.getItem('PDM') == 'true') {
                    $('#PDM').attr()[0].checked = false;
                    localStorage.setItem('PDM', 'false');
                };
                if (localStorage.getItem('CSTMSG') == '') {
                    window.alert('No custom message set. Please open a blank PM window and build your custom template first then select this feature after you have saved it.');
                    $('#CMSG').attr()[0].checked = false;
                };
                if (localStorage.getItem('CSTMSG') !== '') {
                    localStorage.setItem('CMSG', box1.checked);
                };
            };
        };
        let pimg = document.createElement('img');
        pimg.id = 'PM-IMG';
        pimg.src = PMIMG;
        pimg.onmouseout = function() {
            pimg.src = PMIMG;
        };
        pimg.onmouseover = function () {
            pimg.src = PMIMGP;
        };
        pdiv.appendChild(pimg);
        document.getElementById('P-IMG').appendChild(pimg);
        $('#PM-IMG').wrap(a);
        pimg.onclick = function() {
            if (document.getElementById('PDM').checked == true) {
                createMessage();
            }
            if ((document.getElementById('PDM').checked == false) && (document.getElementById('CMSG').checked == false)) {
                createBMessage();
            };
            if (document.getElementById('CMSG').checked == true) {
                custMessage();
            };
        };
        var dimg = document.createElement('img')
        dimg.id = 'DS-IMG';
        dimg.src = DIMG;
        dimg.onmouseout = function() {
            dimg.src = DIMG
        };
        dimg.onmouseover = function () {
            dimg.src = DIMGP
        }
        document.body.appendChild(dimg);
        document.getElementById('P-IMG').appendChild(dimg);
        var beta = '(Beta)'
        $('#DS-IMG').wrap(a);
        $('#wrap').after(pdiv);
        if (DBG == true) {
            dimg.onclick = function() {
                let today = localStorage.getItem('WMEBtoday');
                console.log('Birthday Button for Discord Clicked.\n\nBirthday List Contains: ' + arrBirthdayListDiscord.length + ' usernames')
            };
        } else {
            dimg.onclick = function() {
                let today = localStorage.getItem('WMEBtoday');
                var copyText = 'Birthdays for ' + today + ':\n\n@' + arrBirthdayListDiscord.join( '\n@' ) + '\n\nHappy Birthday everyone!';
                var copied = $('<textarea id="bdcopy" rows="1" cols="1">').val(copyText.replace(/\_*\n/g, '\n')).appendTo('body').select();
                document.execCommand('copy');
                console.log(copyText.replace(/\_*\n/g, '\n'));
                alert('Birthdays Copied for Discord');
                var rembox = document.getElementById('bdcopy');
                document.body.removeChild(rembox);
            };
        };
    };
    function removeMe() {
        $('#postingbox > div > fieldset > div.column1 > dl:nth-child(1) > dd > input').click();
    }
    function addBCCList(bcc_list) {
        $('#username_list').val(bcc_list);
        $('input[name=add_bcc]').click()
        removeMe();
    }
    function init() {
        var checked = JSON.parse(localStorage.getItem('PDM'));
        var checked1 = JSON.parse(localStorage.getItem('CMSG'));
        var sigcheck = JSON.parse(localStorage.getItem('SIG'));
        if ($('#message-box').is(':visible')) {
            createTemplate();
            document.getElementById('attach_sig').checked = sigcheck;
            if (DBG == true) { window.alert('You are in DEBUG Mode for ' + SCRIPTNAME + ' Script\n\nIf this is in error please contact Dude495.');
                              console.log("%cWME Birthdays (DEBUG MODE)", "color: red; font-size:15px;");
                             };
        }
        if (/forum/.test(location.href) && $("h3:contains(Birthdays)").length > 0) {
            BirthdayButton();
            document.getElementById('PDM').checked = checked;
            document.getElementById('CMSG').checked = checked1;
            var WMEBt = $('#page-body > p:nth-child(2)')[0].innerText.replace(todayRegEx, '');
            localStorage.setItem('WMEBtoday', WMEBt);
            if (DBG == true) { window.alert('You are in DEBUG Mode for ' + SCRIPTNAME + ' Script\n\nIf this is in error please contact Dude495.');
                              console.log('%c' + SCRIPTNAME +' (DEBUG MODE)', 'color: red; font-size:15px;');
                             };
        } else if (/forum\/ucp\.php/.test(location.href)) {
            var urlParams = new URLSearchParams(window.location.search);
            if (urlParams.has('bcc_list')) {
                addBCCList(urlParams.get('bcc_list'));
            }
        }
    }
    function bootstrap(tries = 1) {
        if (((/forum/.test(location.href)) && $("h3:contains(Birthdays)").length > 0) || $('#message').is(':visible')) {
            init();
        } else if (tries < 1000) {
            setTimeout(function () {bootstrap(tries++);}, 200);
        }
    }
    bootstrap();
})();
