(function() {
    'use strict';
    // load fonts
    $("head").append("<link href='https://fonts.googleapis.com/css?family=Roboto:400,300,700' rel='stylesheet' type='text/css'>");
    // restore logo
    $("header a.logo img").attr("src","data:image/png;base64,");
    $("header a.logo").innerHTML
    // restore favicon
    $("head").append("<link rel=\"shortcut icon\" href=\"data:image/x-icon;base64,\" />");
    // move stuff around
    $("div.above > div.spotlight").prependTo("div#page");
    //    $("div.above > div.spotlight").insertAfter("div#page-wrapper > div.leaderboards");
    $("nav.primary").appendTo("header div.container");
    $("nav.secondary").appendTo("header div.container");
    // delete stuff
    //$("div.leaderboards").remove();
    //$("div.billboard-container").remove();
    //$("div.spotlight-bar").remove();
})();

