function setCookie(cname, cvalue, exmin) {
  var d = new Date();
  d.setTime(d.getTime() + (exmin * 60 * 1000));
  var expires = 'expires=' + d.toUTCString();
  document.cookie = cname + '=' + cvalue + '; ' + expires;
}
function listCookies()
{
  if (document.cookie === '')
  return false;
  var theCookies = document.cookie.split('; ');
  return theCookies;
}
