    waitChange: function(onCheck, cb, options) {
      "use strict";
      var abort;
      options = options || {
          repeat: 0
        };

      var n = options.count || 12;

      var onCb = function(data) {
        cb.apply(null, arguments);

        if (options.repeat > 0) {
          options.repeat--;
          n = options.count || 12;
          wait();
        }
      };

      var checkFunc = function(data) {
        if (abort) return;

        if (data) {
          return onCb.apply(null, arguments);
        }

        wait();
      };

      var wait = function() {
        n--;
        setTimeout(function() {
          if (abort) return;

          if (n < 0) {
            return onCb();
          }

          onCheck(checkFunc);
        }, options.timer || 500);
      };

      if (!options.skipFirst) {
        onCheck(checkFunc);
      } else {
        wait();
      }

      return {
        abort: function() {
          abort = true;
        }
      }
    },

