// ==UserScript==
// @name         YiXueGuanVideoControl
// @namespace    http://tampermonkey.net/
// @version      0.1
// @description   hotkey for paly/pause video
// @author       You
// @match        https://yxgapp.com/cms/index.html
// @grant        none
// ==/UserScript==
function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}
var oTime;
var nTime;
var ra = new KeyboardEvent("keyup",
                          {
    code: "ArrowRight",
    ctrlKey: true
});
var rm = new KeyboardEvent("keyup",
                          {
    code: "ArrowLeft",
    ctrlKey: true
});
var ll = new KeyboardEvent("keyup",
                          {
    code: "ArrowLeft",
    shiftKey: true
});

$(document).keydown(function(e) {
    'use strict';
    function gv(){
    return  document.getElementsByClassName("vjs-tech")[0];
 }
    if(e.keyCode === 81 && e.ctrlKey ) {
        // Ctrl+Q pressed
        $("div.vjs-control-bar > button.vjs-play-control.vjs-control.vjs-button").remove();
        let myVideo=gv();
        if (myVideo.paused){

            myVideo.play();
        }
        else{
            myVideo.pause();
        }
    }
    if(e.keyCode === 77 && e.ctrlKey ) {
        // Ctrl+M pressed
        document.getElementsByClassName("btn-primary")[0].click();
    }
    if(e.keyCode === 73 && e.ctrlKey ){
        var vTag=gv();
        if (vTag.currentTime < 1){
            vTag.currentTime = 0;
        }
        else{
            let prevTime = vTag.currentTime;
            prevTime = prevTime -3;
            vTag.currentTime = prevTime;
        }
    }
    if(e.keyCode === 36 && e.altKey && e.ctrlKey ){
        let myVid=gv();
        if (myVid.paused){
            myVid.play();
        }
        document.getElementsByClassName("btn-primary")[0].click();
        oTime = Date.now();
        console.log(oTime);
    }
    if(e.keyCode === 33 && e.altKey && e.ctrlKey ){
        let myVideo=gv();
        if (myVideo.paused){
            myVideo.play();
        }
        else{
            myVideo.pause();
        }
        nTime = Date.now();
        console.log(nTime);
        let subInt = Math.floor((nTime - oTime)/100) - 10;
        console.log(subInt);
        if(subInt > 0){
            for(let  i=0;i<subInt;i++){
                window.dispatchEvent(ra);
            }
        }
        else{
            subInt=Math.abs(subInt);
            for(let i=0;i<subInt;i++){
                window.dispatchEvent(rm);
            }
        }
        let leftAdjust = 5;
        for(let l=0;l<leftAdjust;l++){
            window.dispatchEvent(ll);
        }
        beep();
    }

});