var minutes=1;
var soundPath = 'http://ganjawars.ru/sounds/10.mp3';

//***************************************************************************
//Отсылает запрос на проверку сообщения
var st = function start()
{
    var xhr = new XMLHttpRequest();
    xhr.open('GET', encodeURI("home.php"));
    xhr.overrideMimeType('text/xml; charset=windows-1251');
    xhr.onload = function() 
    {
        if(document.getElementById('resp')===null)
        {
            var div = document.createElement( 'div' );
            div.id = 'resp';
            div.style.display = 'none';
            document.getElementsByTagName('body')[0].appendChild( div );
        }
        if (xhr.status === 200)
        {
            document.getElementById('resp').innerHTML=xhr.responseText;
            var respDoc = document.getElementById('resp');
            var els = respDoc.getElementsByTagName('img');
            for( var i = 0; i < els.length; i++ )
            {
                var el = els[i];
                if( el.src.match( 'i\/top\/line\/pismo\.gif' ) )
                {
                    for(var j=0; j<5;j++)
                        beep();
                    el.src = 'data:text/html;charset=utf-8;base64,R0lGODlhDwAKALICAICAgAAAAP%2F%2F%2F%2F8AAAAA%2F4AAAApKfwAAACH%2FC05FVFNDQVBFMi4wAwEAAAAh%2BQQBKAAHACwAAAAADwAKAAADK3i63AcwShgUEDjjMUB9AkFoQlF4FkmiICa%2BI3upmZzBomDTGBv8wCDwkAAAIfkEBRQABwAsAAAAAA8ACgAAAwtoutz%2BMMpJq73YJgA7' ;
                    el.width = 12 ;
                    el.height = 12;
                    setTimeout(function(){alert( el.title )}, 100);
                    break;
                }
            }
        }
        else 
        {
            alert('Request failed.  Returned status of ' + xhr.status);
        }
    };
    xhr.send();
}
var inter = setInterval(st, minutes*1000*60);
var els = document.getElementsByTagName('img');
for( var i = 0; i < els.length; i++ )
{
    var el = els[i];
    if( el.src.match( 'i\/top\/line\/pismo\.gif' ) )
    {
        for(var j=0; j<5;j++)
            beep();
        el.src = 'data:text/html;charset=utf-8;base64,R0lGODlhDwAKALICAICAgAAAAP%2F%2F%2F%2F8AAAAA%2F4AAAApKfwAAACH%2FC05FVFNDQVBFMi4wAwEAAAAh%2BQQBKAAHACwAAAAADwAKAAADK3i63AcwShgUEDjjMUB9AkFoQlF4FkmiICa%2BI3upmZzBomDTGBv8wCDwkAAAIfkEBRQABwAsAAAAAA8ACgAAAwtoutz%2BMMpJq73YJgA7' ;
        el.width = 12 ;
        el.height = 12;
        setTimeout(function(){alert( el.title )}, 300);
        break;
    }
}
function beep() {
    var snd;
    if(!soundPath)
        snd = new Audio("data:audio/wav;base64,");  
    else
    {
        snd = new Audio();
        snd.src = soundPath;
    }
    snd.play();
}
