// ==UserScript==
// @name         Tracer
// @namespace    http://tampermonkey.net/
// @version      0.2
// @description  Watch well known medal stealers
// @author       N.Tsvetkov
// @include      /^https:\/\/www\.erepublik\.com\/[a-z]{2}$/
// @require      http://ajax.googleapis.com/ajax/libs/jquery/2.1.0/jquery.min.js
// @grant        GM_setValue
// @grant        GM_getValue
// @grant        GM_addStyle
// ==/UserScript==

function style(t) {
    $("head").append("<style>" + t + "</style>");
}

function getData(id) {
    $.ajax({
        url : "/en/citizen/profile/" + id,
    })
        .done(function (h) {
        var name = $(h).find('.citizen_profile_header h2').clone().children().remove().end().text().trim(),
            status = ($(h).find('.online_status').hasClass('on')) ? 'on' : 'off',
            location = $(h).find('.citizen_info a:first, .citizen_info a:eq(1)').text().trim();
        if (typeof locations[id] != 'undefined' && locations[id] != location) {
            beep();
            status = 'moved';
        }
        $('#pdr').append('<div class="' + status + '">' + name + ' - ' + location + '</div>');
        locations[id] = location;
    });
}

function checkPlayers(players) {
    $('#pdr').html('');
    $(players).each(function(id, player){
        getData(player);
    });
}

function closeModal() {
    $('#myModal').css('display', 'none');
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}

var locations = [];

(function() {
    'use strict';
    style("#pederi{z-index: 99999; position: absolute; top: 0; right: 0;margin: 7px;padding: 5px;border-radius: 3px;font-size: 11px;background-color:rgba(255,255,255,0.8);border:1px solid #999;box-shadow: 10px 10px 5px #888888;cursor:pointer;};");
    style(".on{color: green;}");
    style(".moved{color: red;}");
    style('.modal {z-index: 99999;display: none; position: fixed; left: 0; top: 0; width: 100%; height: 100%; overflow: auto; background-color: rgb(0,0,0); background-color: rgba(0,0,0,0.4);');
    style('.modal-content {background-color: #fefefe; margin: 15% auto; padding: 20px; border: 1px solid #888; width: 600px;');
    style('.modal-content label {display: block; font-family:Verdana;font-size: 11px; font-weigth:700;}');
    style('.cls {color: #aaa; float: right; font-size: 28px; font-weight: bold;line-height: 16px;');
    style('.cls:hover, .cls:focus {color: black; text-decoration: none; cursor: pointer;}');
    style('#ids{width: 400px;}');
    $('body').after("<div id='pederi'><div id='pdr'></div></div>");
    $('body').after('<div id="myModal" class="modal"><div class="modal-content"><span class="cls">&times;</span><p><label>Enter comma separated ids:</label><input type="text" id="ids"><button id="idb">Save</button></p></div></div>');

    var dPlayers = new Array(5463210, 9123573, 2064778);
    var sPlayers = GM_getValue('players', dPlayers.join(','));
    var players = sPlayers.split(',');

    $("#pederi").click(function() {
        $('#ids').val(sPlayers);
        $('#myModal').css('display', 'block');
    });
    $(".cls").click(function(){
        closeModal();
    });
    $(window).click(function(event) {
        if (event.target.id == 'myModal') {
            closeModal();
        }
    });
    $('#idb').click(function() {
        sPlayers = $('#ids').val();
        players = sPlayers.split(',');
        checkPlayers(players);
        GM_setValue('players', sPlayers);
        closeModal();
    });
    checkPlayers(players);
	setInterval(function() {
		checkPlayers(players);
	}, 40e3);

})();