function addUrlArgs (url, args){
    if (!args) return url;
    if (url.indexOf('?') < 0)
        url += '?';
    else if (url.substr(url.length-1) != '&')
        url += '&';    
    if (matTypeof(args == 'object')) return url + implodeUrlArgs (args);    
    return url + args;
};

