searches.push(ap = {alt:"Anime-Planet", template:"https://www.anime-planet.com/search.php?search_type=all&search=%title%", img:apimg});
searches.push(an = {alt:"AnimeNfo", template:"https://www.animenfo.com/search.php?&queryin=anime_titles&option=keywords&query=%title%", img:animg});
