                    message[i].getElementsByClassName('addGT')[0].getElementsByTagName('a')[1].style.color="rgba(250,0,0,0.6)";
                    message[i].getElementsByClassName('addGT')[0].getElementsByTagName('a')[1].style.fontWeight="bold";

