    $('td.seeders').each(function(i, obj) {
        var leechers = parseInt($(this).next(".leechers").text());
        var seeders = parseInt($(this).text());

        if (leechers > 0) {
            $(this).next(".leechers").find("a").css('color', 'yellow');
        };
        if (seeders > 0) {
            $(this).find("a").css('color', 'green');
        };

        if( seeders > leechers) {
            $(this).find("a").css('color', 'green');
        };
        if( seeders == leechers && seeders != 0) {
            $(this).find("a").css('color', 'yellow');
        };
        if( seeders < leechers) {
//            $(this).find("a").css('color', 'red');
            $(this).next(".leechers").find("a").css('color', 'red');
        };
        if( seeders == 0) {
            $(this).find("a").css('color', 'red');
            $(this).next(".leechers").find("a").css('color', 'red');
        };


        if ( (seeders / leechers) < 1) {
            $(this).next(".leechers").find("a").css('color', 'red');
        };

        if( leechers == 0) {
            $(this).next(".leechers").find("a").css('color', 'green');
        };
    });

    function clean(node)
{
  for(var n = 0; n < node.childNodes.length; n ++)
  {
    var child = node.childNodes[n];
    if
    (
      child.nodeType === 8
      ||
      (child.nodeType === 3 && !/\S/.test(child.nodeValue))
    )
    {
      node.removeChild(child);
      n --;
    }
    else if(child.nodeType === 1)
    {
      clean(child);
    }
  }
}
    clean(document.body);

