// ==UserScript==
// @name         Real-Debrid multi-upload torrents
// @namespace    http://tampermonkey.net/
// @version      0.1
// @description  Add multi-upload to torrents service of real-debrid, when you send file it will use the hoster and splitting above. After all uploads the page will refresh. Tested on chrome.
// @author       gilbert1995
// @match        https://real-debrid.com/torrents
// @grant        none
// @require https://cdnjs.cloudflare.com/ajax/libs/jquery/1.12.4/jquery.min.js
// @require https://cdnjs.cloudflare.com/ajax/libs/jquery-easing/1.3/jquery.easing.min.js
// @require https://cdnjs.cloudflare.com/ajax/libs/blueimp-file-upload/9.12.5/js/vendor/jquery.ui.widget.min.js
// @require https://cdnjs.cloudflare.com/ajax/libs/blueimp-file-upload/9.12.5/js/jquery.iframe-transport.min.js
// @require https://cdnjs.cloudflare.com/ajax/libs/blueimp-file-upload/9.12.5/js/jquery.fileupload.min.js
// ==/UserScript==

(function() {
    'use strict';
    var link = '<style>#upload ul li u,a:hover{text-decoration:none}*{margin:0;padding:0}html{box-sizing:border-box;background-color:#ebebec;background-image:-webkit-radial-gradient(center,#ebebec,#b4b4b4);background-image:-moz-radial-gradient(center,#ebebec,#b4b4b4);background-image:radial-gradient(center,#ebebec,#b4b4b4);min-height:900px}*,:after,:before{box-sizing:inherit}body{font:15px/1.3 Arial,sans-serif;color:#4f4f4f}a,a:visited{outline:0;color:#389dc1}aside,footer,header,section{display:block}#upload{font-family:\'PT Sans Narrow\',sans-serif;background-color:#373a3d;background-image:-webkit-linear-gradient(top,#373a3d,#313437);background-image:-moz-linear-gradient(top,#373a3d,#313437);background-image:linear-gradient(top,#373a3d,#313437);padding:30px;border-radius:3px;box-shadow:0 0 10px rgba(0,0,0,.3)}#drop{background-color:#2E3134;padding:40px 50px;margin-bottom:30px;border:20px solid transparent;border-radius:3px;border-image:url(data:image/png;base64,) 25 repeat;text-align:center;text-transform:uppercase;font-size:16px;font-weight:700;color:#7f858a}#drop a{background-color:#007a96;padding:12px 26px;color:#fff;font-size:14px;border-radius:2px;cursor:pointer;display:inline-block;margin-top:12px;line-height:1}#drop a:hover{background-color:#0986a3}#drop input,#upload ul li input{display:none}#upload ul{list-style:none;margin:0 -30px;border-top:1px solid #2b2e31;border-bottom:1px solid #3d4043}#upload ul li{background-color:#333639;background-image:-webkit-linear-gradient(top,#333639,#303335);background-image:-moz-linear-gradient(top,#333639,#303335);background-image:linear-gradient(top,#333639,#303335);border-top:1px solid #3d4043;border-bottom:1px solid #2b2e31;padding:15px;position:relative}#upload ul li p{overflow:hidden;white-space:nowrap;color:#EEE;font-size:16px;font-weight:700;margin-top:20px}#upload ul li b{position:absolute;top:20px;right:60px;font-size:42px;color:#fff}#upload ul li u{position:absolute;right:60px;top:63px}#upload ul li div{background:#0788a5;width:65%;height:4px;bottom:0;left:0;position:absolute}#upload ul li i{font-weight:400;font-style:normal;color:#7f7f7f;display:block}#upload ul li canvas{margin-top:15px;margin-left:32px}#upload ul li span{width:15px;height:12px;background:url(data:image/png;base64,) no-repeat;top:34px;right:33px;position:absolute;cursor:pointer}#upload ul li.working span{height:16px;background-position:0 -12px}#upload ul li.error p{color:red}</style>';
    $('head').append(link);
    link = '<link rel="stylesheet" type="text/css" href="https://fonts.googleapis.com/css?family=PT+Sans+Narrow:400,700">';
    $('head').append(link);

    // Your code here...
    $("#wrapper_global > div > div > form").after('<form id="upload" method="post" action="https://real-debrid.com/torrents" enctype="multipart/form-data"style="clear: both;top: 15px;position: relative;"><div id="drop">Drop Here<a>Browse</a><input type="file" name="file" multiple /></div><ul><!-- The file uploads will be shown here --></ul></form>');

    var ul = $('#upload ul');
    
    $('#drop a').click(function(){
        // Simulate a click on the file input button
        // to show the file browser dialog
        $(this).parent().find('input').click();
    });

    // Initialize the jQuery File Upload plugin
    $('#upload').fileupload({

        // This element will accept file drag/drop uploading
        dropZone: $('#drop'),

        formData: {splitting_size: $("#splitting_size").val(), hoster: $("#hoster").val()},

        // This function is called when a file is added to the queue;
        // either via the browse button, or via drag/drop:
        add: function (e, data) {

            var tpl = $('<li class="working"><p></p><span></span><b>0%</b><u></u><div></div></li>');

            // Append the file name and file size
            tpl.find('p').text(data.files[0].name)
                         .append('<i>' + formatFileSize(data.files[0].size) + '</i>');

            // Add the HTML to the UL element
            data.context = tpl.appendTo(ul);

            // Listen for clicks on the cancel icon
            tpl.find('span').click(function(){

                if(tpl.hasClass('working')){
                    jqXHR.abort();
                }

                tpl.fadeOut(function(){
                    tpl.remove();
                });

            });

            // Automatically upload the file once it is added to the queue
            var jqXHR = data.submit();
        },

        progress: function(e, data){

            // Calculate the completion percentage of the upload
            var progress = parseInt(data.loaded / data.total * 100, 10);

            // Update the hidden input field and trigger a change
            // so that the jQuery knob plugin knows to update the dial
            data.context.find('b').html(progress + "%");
            data.context.find('div').css('width', progress + "%");
			data.context.find('u').html(formatFileSize(data.bitrate/8) + '/s');

            if(progress == 100){
                data.context.removeClass('working');
            }
        },

        fail:function(e, data){
            // Something has gone wrong!
            data.context.addClass('error');
        },
        
        stop: function (e) {
            console.log('Uploads finished');
            window.location.href = '';
        }

    });


    // Prevent the default action when a file is dropped on the window
    $(document).on('drop dragover', function (e) {
        e.preventDefault();
    });

    // Helper function that formats the file sizes
    function formatFileSize(bytes) {
        if (typeof bytes !== 'number') {
            return '';
        }

        if (bytes >= 1000000000) {
            return (bytes / 1000000000).toFixed(2) + ' GB';
        }

        if (bytes >= 1000000) {
            return (bytes / 1000000).toFixed(2) + ' MB';
        }

        return (bytes / 1000).toFixed(2) + ' KB';
    }
    
})();