// ==UserScript==
// @name        Drudge Link Enhancements
// @namespace   drudgereport.com
// @description Enhances links on Drudge Report and neuters links to fake-news sites
//
// @include     http://*.drudgereport.com/*
// @include     http://drudgereport.com/*
// @version     0.2.6
// @grant       GM_addStyle
// @run-at      document-end
// @license     MIT License
// @require     https://ajax.googleapis.com/ajax/libs/jquery/1.11.3/jquery.min.js
// ==/UserScript==

$("document").ready(function () {
  GM_addStyle('.fake-news-prefix { font-size: 0.8em; display: inline; }');
  GM_addStyle('.fake-news { font-size: 0.8em; }');
  GM_addStyle('.fake-news-icon { display: inline; }');

  var fakeNewsDomains = [
    'abcnews.com',
    'abcnews.go.com',
    'americanprogress.org',
    'americanprogressaction.org',
    'bbc.co.uk',
    'bbc.com',
    'bbcnews.com',
    'bloomberg.com',
    'businessinsider.com',
    'buzzfeed.com',
    'cbc.ca',
    'cbslocal.com',
    'cbsnews.com',
    'chicagotribune.com',
    'cnbc.com',
    'cnn.com',
    'dailykos.com',
    'economist.com',
    'eutimes.net',
    'ft.com',
    'gawker.com',
    'huffingtonpost.com',
    'latimes.com',
    'littlegreenfootballs.com',
    'mediaite.com',
    'mediamatters.org',
    'motherjones.com',
    'moveon.org',
    'msn.com',
    'msnbc.com',
    'nbcnews.com',
    'news.sky.com',
    'news.vice.com',
    'newsweek.com',
    'newyorker.com',
    'npr.org',
    'nydailynews.com',
    'nytimes.com',
    'pbs.org',
    'politico.com',
    'rawstory.com',
    'rollingstone.com',
    'salon.com',
    'seattletimes.com',
    'slate.com',
    'superstation95.com',
    'thedailybeast.com',
    'theguardian.com',
    'theyoungturks.co.uk',
    'thinkprogress.org',
    'time.com',
    'tytnetwork.com',
    'usatoday.com',
    'vice.com',
    'washingtonpost.com',
    'wonkette.com',
    'yahoo.com',
  ];

  var icon = "<img class='fake-news-icon' src='data:image/png;base64,' style='margin-right: 6px; vertical-align: middle;' />";
  var title = "Fake news sites, like this one, have been tagged";
  var linkStats = {};

  function extractDomain(url) {
    var domain;
    if (url.indexOf("://") > -1) {
      domain = url.split('/')[2];
    } else {
      domain = url.split('/')[0];
    }
    domain = domain.split(':')[0];
    return domain;
  }

  function appendLinkStats(stats) {
    var tuples = [];
    var statsHtml = '<h3>Link Stats</h3>';

    for (var key in stats) {
      tuples.push([key, stats[key]]);
    }

    // Reverse sort
    tuples.sort(function(a, b) {
      a = a[1];
      b = b[1];
      return a < b ? 1 : (a > b ? -1 : 0);
    });

    for (var i=0; i<tuples.length; i++) {
      var key = tuples[i][0]
      var val = tuples[i][1];
      statsHtml += "<div class='linkstat-entry'>"+key+" == "+val+"</div>"
    }

    $('body').append(statsHtml);
  }

  function isFakeNewsLink(domain) {
    for (var i = 0; i < fakeNewsDomains.length; i++) {
      if (domain.indexOf(fakeNewsDomains[i]) >= 0) {
        return true;
      }
    }
    return false;
  }

  function tagFakeNewsLink(link, domain) {
    var originalUrl = null;
    if (isFakeNewsLink(domain)) {
      // console.log("replacing fake news: " + domain);
      originalUrl = link.attr("href");
      // console.log("original link: " + originalUrl);
      link.replaceWith(
        '<a href="'+originalUrl+'" title="click this turd pile to go there anyway">'+icon+'</a>' +
        "<div title='click the turd pile to go there anyway' class='fake-news-prefix'>"+domain+" Fake-News link removed</div> <div title='"+title+"' class='fake-news'>" + link.text() + "</div>"
      );
    } else {
      // Add a popup with the link's domain name
      link.attr("title", domain);
    }
  }

  $("a").each(function() {
    var link = $(this);
    var href = link.attr("href");
    var domain = extractDomain(href);
    // console.log(domain);

    tagFakeNewsLink(link, domain);

    // if (linkStats[domain] == null) {
    //   linkStats[domain] = 1;
    // } else {
    //   linkStats[domain] += 1;
    // }
  });

  // appendLinkStats(linkStats);
});
