    function url( site )
    {
        var t = encodeURIComponent( title );
        var i = imdbId;
        var y = year;

        site = site.replace( /%title%/, t );
        site = site.replace( /%imdbid%/, i );
        site = site.replace( /%year%/, y );

        return site;
    }


    function html( $group, displayType, marginRight )
    {
        var h = '';
        var f, s;
        if ( $group == "movies")
        {
            for( var i = 0, len = sites.length; i < len; i++ )
            {
                s = sites[i];
                if ( s.movies == '1' && s.status == 'active' )
                {
                    h += '<li style="padding-right: 6px;"><a style="height: 18px; z-index: 10; display: inline-flex; " href="' + url( s.url ) + '" target="_blank"><img style="width: 18px; height: 18px; opacity:0.8;" src="' + s.icon + '" title="' + s.name + '"><\/a></li>';
                }
            }
            f = '<ul name="script_element" style="position: relative; margin: 0; list-style-type: none; display: inline-flex; padding: 0; vertical-align: middle; margin: 1em 0 0.5em 0">' + h + '</ul>';
        }

        else if ( $group == "lists" || $group == "your_lists" )
        {
            for( i = 0, len = sites.length; i < len; i++ )
            {
                s = sites[i];
                if ( s[$group] == '1' && s.status == 'active' )
                {
                    h += '<li style="padding-right: 5px;"><a style="display: inline-flex; " href="' + url( s.url ) + '" target="_blank"><img style="width: 14px; height: 14px; opacity:0.3;" onMouseover="this.style.opacity=1" onMouseout="this.style.opacity=0.3" src="' + s.icon + '" title="' + s.name + '"><\/a></li>';
                }
            }
            f = '<div name="script_element" style="margin: 0.3em 0 0 ' + marginRight + 'em; display:' + displayType + '; height: 15px;"><ul style="position: relative; margin: 0; list-style-type: none; height: 15px; z-index: 10; display: inline-flex;">' + h + '</ul></div>';
        }

        return f;
    }


    function titleEdit( e_title )
    {
        e_title = e_title
            .replace( ":", " " )
            .replace( "-", " " )
            .replace("&amp;","&") //replace & with code
            .replace("&nbsp;","") //delete nobreak space
            .replace(/[\/\\#+()$~%"*?<>{}]/g, " ") //remove bad chars
            .replace( /\s{2,}/g, " " )
            .trim()
        ;
        return e_title;
    }

    function waitForKeyElements (
        selectorTxt,    /* Required: The jQuery selector string that
                            specifies the desired element(s).
                        */
        actionFunction, /* Required: The code to run when elements are
                            found. It is passed a jNode to the matched
                            element.
                        */
        bWaitOnce,      /* Optional: If false, will continue to scan for
                            new elements even after the first match is
                            found.
                        */
        iframeSelector  /* Optional: If set, identifies the iframe to
                            search.
                        */
    ) {
        var targetNodes, btargetsFound;

        if (typeof iframeSelector == "undefined")
            targetNodes     = $(selectorTxt);
        else
            targetNodes     = $(iframeSelector).contents ()
                                               .find (selectorTxt);

        if (targetNodes  &&  targetNodes.length > 0) {
            btargetsFound   = true;
            /*--- Found target node(s).  Go through each and act if they
                are new.
            */
            targetNodes.each ( function () {
                var jThis        = $(this);
                var alreadyFound = jThis.data ('alreadyFound')  ||  false;

                if (!alreadyFound) {
                    //--- Call the payload function.
                    var cancelFound     = actionFunction (jThis);
                    if (cancelFound)
                        btargetsFound   = false;
                    else
                        jThis.data ('alreadyFound', true);
                }
            } );
        }
        else {
            btargetsFound   = false;
        }

        //--- Get the timer-control variable for this selector.
        var controlObj      = waitForKeyElements.controlObj  ||  {};
        var controlKey      = selectorTxt.replace (/[^\w]/g, "_");
        var timeControl     = controlObj [controlKey];

        //--- Now set or clear the timer as appropriate.
        if (btargetsFound  &&  bWaitOnce  &&  timeControl) {
            //--- The only condition where we need to clear the timer.
            clearInterval (timeControl);
            delete controlObj [controlKey]
        }
        else {
            //--- Set a timer, if needed.
            if ( ! timeControl) {
                timeControl = setInterval ( function () {
                        waitForKeyElements (    selectorTxt,
                                                actionFunction,
                                                bWaitOnce,
                                                iframeSelector
                                            );
                    },
                    300
                );
                controlObj [controlKey] = timeControl;
            }
        }
        waitForKeyElements.controlObj   = controlObj;
    }

