	function html( $group, $type, $icons )
	{
        var f, i, s, len, icon;
	    var h = '';
        var count = 0;
        if ( $group == "alpha")
        {
            for( i = 0, len = sites.length; i < len; i++ )
            {
                s = sites[i];
                if ( s.alpha == '1' && s.status == 'active' && s[$type] == '1' )
                {
                    count++;
                    h += '<li style="padding-right: 5px; height: 20px;"><a style="height: 21px; line-height: 21px; display: inline-flex; " href="' + url( s.url ) + '" target="_blank"><img style="width: 21px; height: 21px; opacity:0.7;" onMouseover="this.style.opacity=1" onMouseout="this.style.opacity=0.7" src="' + s[$icons] + '" title="' + s.name + '"><\/a></li>';
                }
            }
            f = '<div id="portalAlpha648" style="margin: 5px 0 10px 0; height: 20px;"><ul style="position: relative; list-style-type: none; display: inline-flex;">' + h + '</ul></div>';
        }

        else if ( $group == "beta" )
        {
            for( i = 0, len = sites.length; i < len; i++ )
            {
                s = sites[i];
                if ( s.beta == '1' && s.status == 'active' && s[$type] == '1' )
                {
                    count++;
                    h += '<li style="padding: 0 5px 0 1px"><a style="height: 20px; line-height: 20px; z-index: 10; display: inline-flex; " href="' + url( s.url ) + '" target="_blank"><img style="width: 20px; height: 20px; opacity:0.7;" onMouseover="this.style.opacity=1" onMouseout="this.style.opacity=0.7" src="' + s[$icons] + '" title="' + s.name + '"><\/a></li>';
                }
            }
            f = '<ul id="portalBeta648" style="list-style-type: none; margin: 8px 0px 5px 0; height: 20px; float: right; display: inline-flex;">' + h + '</ul>';
        }

       else if ( $group == "kisi" )
       {
           for( i = 0, len = sites.length; i < len; i++ )
           {
               s = sites[i];
               if ( s.status == 'active' && s[$type] == '1' && s.alpha == '1' )
               {
                   count++;
                   h += '<li style="padding-right: 6px; height: 20px;"><a style="height: 20px; line-height: 20px; display: inline-flex; " href="' + url( s.url ) + '" target="_blank"><img style="width: 20px; height: 20px; opacity:0.7;" onMouseover="this.style.opacity=1" onMouseout="this.style.opacity=0.7" src="' + s[$icons] + '" title="' + s.name + '"><\/a></li>';
               }
           }
           f = '<div id="portalKisi648" style="margin: 5px 0 10px 0; height: 20px; float: right;"><ul style="position: relative; list-style-type: none; display: flex;">' + h + '</ul></div>';
       }

        if (count > 1) {
            return f;
        }
        else {
            return "";
        }
	}


    function url( site )
	{
        var t = title;
        var tt = ttImdbId;
        var m = malId;
        var y = year;
        var ty = title_year;
        var rt = rotten;
        var mt = meta;

        site = site
            .replace( /%title%/, t )
            .replace( /%imdbid%/, tt.replace( /tt|nm/, "" ) )
            .replace( /%ttimdbid%/, tt )
            .replace( /%malid%/, m )
            .replace( /%year%/, y )
            .replace( /%title_year%/, ty )
            .replace( /%rotten%/, rt )
            .replace( /%meta%/, mt )
            .trim()

        return site;
    }

