    function forumImdbTag() {

        // If there is old element, remove them
        removeThis( '#forumImdbTag648', 0 );

        // Work on each Tag
        var elBox = document.querySelectorAll( '.imdb_wrapper');
        for ( var x = 0; x < elBox.length; x++ )
        {
            // Vars
            var type = elBox[x].getAttribute( "data-type" );
            type = type.replace( "movie", "film" ).replace( "tvseries", "dizi" ).replace( "person", "kisi" ).replace( "TVEpisode", "bolum" );
            var ttImdbId = elBox[x].getAttribute( 'data-id' );
            var title_year = elBox[x].querySelector( 'h2.ipsType_pageTitle' ).textContent.trim().replace( /(.+)\((\d{4})(-|–)?(\d{4})?\)/, "$1 $2" ).replace( /\s{2,}/g, " " );
            var year = title_year.replace( /(.+) (\d{4})$/, "$2").trim();
            var title = title_year.replace( /(.+) (\d{4})$/, "$1").trim();
            if (type == "dizi") {title_year = title;}

            // Drama Check
            var countryInfo = null;
            var infos = elBox[x].querySelectorAll( '[class="ipsColumn ipsDataItem_size5"] + div' );
            if (type == "dizi") {countryInfo = infos[3].textContent.trim();}
            var dramaCheck = false;
            if (countryInfo && countryInfo.search( /(Amerika|United States|İngiltere|United Kingdom)/i ) < 0 && countryInfo.search( /(Güney Kore|Korea, Republic Of|Japonya|Japan|Tayland|Thailand|Çin|China|Tayvan|Taiwan|Hong Kong)/i ) >= 0) {dramaCheck = true;}

            title = title
                .replace( " Pek Yakında", "" )
                .replace( /\(.+\)/, "" )
                .replace("&amp;","&") //replace & with code
                .replace("&nbsp;","") //delete nobreak space
                .replace( /[\/\\#+()$~%"*?<>{}]/g, " ") //remove bad chars
                .replace( /\s{2,}/g, " " )
                .trim()
        ;

            // Calculate the empty area for area which script will be placed
            var statisticsArea = elBox[x].querySelector( 'div.imdb_title > ol' );
            var statisticsAreaWidth = statisticsArea.offsetWidth;
            var statisticsMarginPaddingWidth = parseInt($('div.imdb_title > ol > li').css("marginRight").replace('px', '')) + parseInt($('div.imdb_title > ol > li').css("paddingRight").replace('px', ''));
            var olObjects = statisticsArea.querySelectorAll( 'li' );
            var statisticsWidth = 0;
            for ( var a = 0; a < olObjects.length; a++ ) {statisticsWidth += olObjects[a].offsetWidth + statisticsMarginPaddingWidth;};
            var emptyAreaWidth = statisticsAreaWidth - statisticsWidth;
//            console.log( "statisticsAreaWidth (" + statisticsAreaWidth + ") - statisticsWidth (" + statisticsWidth + ") = emptyAreaWidth (" + emptyAreaWidth + ")" )

            // Fatal Blow
            if (dramaCheck) { statisticsArea.insertAdjacentHTML( "beforeend", html_forum( dramaSites_imdbTag, type, title, year, title_year, ttImdbId, emptyAreaWidth, icons ) ); }
            else { statisticsArea.insertAdjacentHTML( "beforeend", html_forum( sites_imdbTag, type, title, year, title_year, ttImdbId, emptyAreaWidth, icons ) ); }

            // IMDb Score Link
            var imdbScoreWidth = elBox[x].querySelector( '[class*="imdb_wrapper"] > [class*="imdb_score"]' ).offsetWidth;
            var url;
            if (type == "kisi") {url = "https://www.imdb.com/name/" + ttImdbId + "/"} else {url = "https://www.imdb.com/title/" + ttImdbId + "/"}
            var button = '<a id="forumImdbTag648" href="' + url + '" target="_blank" style="position: absolute; z-index: 15; margin: 0; float: right; opacity: 0; height:33px; width: ' + imdbScoreWidth + 'px; top: 14px; right: 14px; background-color: coral; border-radius: 10px;"></a>'
            var butonArea = elBox[x].querySelector( 'div.imdb_score' ).parentElement;
            butonArea.insertAdjacentHTML( "afterbegin", button )
            var titleSpace = statisticsAreaWidth - imdbScoreWidth - 10;
//            console.log( "statisticsAreaWidth (" + statisticsAreaWidth + ") - imdbScoreWidth (" + imdbScoreWidth + ") = titleSpace (" + titleSpace + ")" );
            elBox[x].querySelector( 'h2.ipsType_pageTitle' ).style.width = titleSpace + "px"; // arrange title width to not overlap with rating

        }
    }


	function html_forum( g_sites, g_type, g_title, g_year, g_title_year, g_ttImdbId, g_emptyAreaWidth, g_icons )
	{
		var h = '';
        var sitesnumber = 0;
		for( var i = 0, len = g_sites.length; i < len; i++ )
		{
			var s = g_sites[i];
            if (s[g_type] == "1")
            {
                var icon = s.icon;
                var heightWidth = 19; var liPaddingRight = 4; var butonArea = heightWidth + liPaddingRight;
                h += '<li style="padding-right: ' + liPaddingRight + 'px;"><a href="' + url_forum( s.url, g_title, g_year, g_title_year, g_ttImdbId ) + '" target="_blank"><img style="width: ' + heightWidth + 'px; vertical-align: top; opacity:0.6;" onMouseover="this.style.opacity=1" onMouseout="this.style.opacity=0.6" src="' + icon + '" title="' + s.name + '"></a></li>';
                sitesnumber++;
            }
        }
        var ul = isBrNeeded( g_emptyAreaWidth, butonArea, sitesnumber ) + ' display: inline-flex; padding: 0; list-style-type: none">' + h + '</ul>';
		return ul;
	}

    function isBrNeeded( i_emptyAreaWidth, i_butonArea, i_sitesnumber )
    {
        var scriptUlWidth = i_butonArea * i_sitesnumber;
        if ( (i_emptyAreaWidth - scriptUlWidth) < 10) {return '<br id="forumImdbTag648"><ul id="forumImdbTag648" style="margin: 5px 0 0 0;';}
        else { return '<ul id="forumImdbTag648" style="float: right; margin: 0 0 0 0;'; }
    }


    function url_forum( g_site, g_title, g_year, g_title_year, g_ttImdbId )
	{
        g_site = g_site.replace( /%title%/, g_title );
        g_site = g_site.replace( /%year%/, g_year );
        g_site = g_site.replace( /%title_year%/, g_title_year );
        g_site = g_site.replace( /%imdbid%/, g_ttImdbId.replace( /tt|nm/, "" ) );
        g_site = g_site.replace( /%ttimdbid%/, g_ttImdbId );

		return g_site;
    }

