    function removeThis ( selector, nth_parent ) {
        var add_array = document.querySelectorAll( selector )
        if ( add_array )
        {
        for ( var t = 0; t < add_array.length; t++ )
        {
            var add = add_array[t]
            for ( var x = 0; x < nth_parent; x++ )
            {add = add.parentNode}
            add.parentNode.removeChild(add)
        }
        }
    }


    function getFirstItem(input, key, value) {
    for(var i = 0; i < input.length; i++) {
        var obj = input[i]
        if(obj[key] == value) {return i;}
    } return null;
    }

