    var gilfoidCSS = document.createElement("style");
    gilfoidCSS.type = "text/css";
    gilfoidCSS.innerHTML = "\
        #art-main{ \
            background-color: #ffffff; \
			background-image: url('data:image/png;base64,'); \
		}\
		body{\
			background-color: #ffffff; \
		}"
