// ==UserScript==
// @name			Netgear Router Interface Advancement
// @namespace		NRIA
// @version			1.0
// @description		Modify the interface for better overview
// @include			http://*.routerlogin.net/*
// @author			wuddih
// @grant			GM_addStyle
// ==/UserScript==

var arrow = "data:image/gif;base64,R0lGODlhDAAMALMAAFAyJP8AAM0AAOEAALgAAKYAAPMAAJoAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAAAgALAAAAAAMAAwAAAQsEMlJq70TYAmCpl93eBmJAEVBdoQJEC0ACHRICxowhMGwZ5yAwfCxdIoXZAQAOw==";
var backg = "data:image/png;base64,";
var headlogo = "data:image/png;base64,";


// Global Styling
if (window.location.href.indexOf("routerlogin") != -1) {
	var images = document.getElementsByTagName("img");
	var x = 0;
	while(x < images.length) {
		if(images[x].src.indexOf("menublue.gif") != -1 || images[x].src.indexOf("darkblue.gif") != -1) {
			images[x].parentNode.removeChild(images[x]);
		}
		if(images[x].src.indexOf("settings.gif") != -1) {
			GM_addStyle('br { display: none; }');
			images[x].src = headlogo;
		}
	x++;
	}
	var td = document.getElementsByTagName("td");
	var x = 0;
	while(x < td.length) {
		if(td[x].getAttribute('background') == "liteblue.gif") {
			td[x].parentNode.removeChild(td[x]);
		}
	x++;
	}
	GM_addStyle('body { background: url("'+ backg +'") fixed; }');
	GM_addStyle('img { vertical-align: text-top; }');
	GM_addStyle('h1 { font: bold 1.0em; color: #3F3F3F; text-decoration: underline; background: none; }');
	GM_addStyle('a { background: none; }');
}

// Modify Global Frameset
if (window.location.href.indexOf("start.htm") > 0) {
	var framesets = document.getElementsByTagName("frameset");
	framesets[0].rows = "90,*";
	framesets[1].cols = "150,100%,290";
	var x = 0;
	while(x < framesets.length) {
		framesets[x].setAttribute("frameborder","0");
		framesets[x].setAttribute("framespacing","0");
		x++;
	}
}

// Frameset Fix for Basic Settings
if (window.location.href.indexOf("BAS_basic.htm") > 0) {
	var framesets = document.getElementsByTagName("frameset");
	framesets[0].rows = "120,*";
}

// Modify Menu
if (window.location.href.indexOf("MNU_menu.htm") > 0) {
	var images = document.getElementsByTagName("img");
	var x = 0;
	while(x < images.length) {
		if(images[x].src.indexOf("redbull.gif") != -1) {
		images[x].src = arrow;
		}
	x++;
	}
	GM_addStyle('big { font: bold 1.0em; color: #3F3F3F; text-decoration: underline; }');
	GM_addStyle('a { font-size: 0.9em; font-weight: bold; color: #FE6666; }');
	GM_addStyle('a:hover { text-decoration: underline }');
	GM_addStyle('a:active { text-decoration: overline underline }');
}

// Modify Blocklist
if (window.location.href.indexOf("keyword") > 0) {
	var select = document.getElementsByTagName("select");
	select[0].size = 16;
}

// Modify Textareas
if (window.location.href.indexOf("log") > 0) {
	GM_addStyle('textarea { font-family: "Consolas"; width: 600px; height: 1060px; font-size: 10px; }');
}

// Modify Help Frame
if (window.location.href.indexOf("_h.htm") > 0) {
	GM_addStyle('h1 { font-size: 0.7em; font-weight: bold; text-decoration: underline; }');
	GM_addStyle('p { font-size: 0.6em; }');
	GM_addStyle('li { font-size: 0.8em; }');
}