// ==UserScript==
// @name           altyazı 
// @namespace      altyazıvetorrent
// @description    altyazı ve torrent arama
// @include     http://www.imdb.com/title/*
// @version 0.02
// ==/UserScript==
(function() {

	'use strict';

	// Possible placeholders in URL: %id, %title and %title[padding] (e.g. %title[_])
	// Inserting null creates a seperator.
	// Favicon converter: http://dataurl.net/#dataurlmaker

var sites = [
		{name:'Türkçe Altyazı', url:'http://www.turkcealtyazi.org/find.php?cat=sub&find=tt%id', icon:'data:image/x-icon;base64,%3D%3D'},
        {name:'PlanetDP', url:'https://www.planetdp.org/movie/search?title=tt%id', icon:'data:image/png;base64,'},
        {name:'RarBG', url:'http://rarbg.com/torrents.php?search=tt%id', icon:'data:image/png;base64,' },
        {name:'Zamunda', url:'http://zamunda.net/bananas?search=tt%id', icon:'data:image/png;base64,' },
        {name:'torrentzeu', url:'https://torrentz.ht/search?f=%title', icon:'data:image/png;base64,'},
        {name:'piratebay', url:'https://thepiratebay.org/search/tt%id', icon:'data:image/jpeg;base64,'},
        {name:'isohunt', url:'https://isohunt.to/torrents/?ihq=%title', icon:'data:image/jpeg;base64,' },
    
    
   
   
    ];

  

	var title = document.title.match(/^(.*) \(/)[1];
	var id    = location.pathname.match(/\/tt(\d+)\//)[1];

	function html() {
		var h = '';
		for(var i = 0, len = sites.length; i < len; i++) {
			var s = sites[i];
			h += s ? '<span><a href="' + url(s) + '" target="_blank"><img src="' + s.icon + '" title="' + s.name + '"><\/a>&nbsp;</span>' : '&nbsp;&nbsp;';
		}
		return h;
	}

	function url(site) {
		var u = site.url.replace(/%id/, id);
		if(u.match(/%title\[(.*?)\]/)) {
			return u.replace(/%title\[.*?\]/, encodeURIComponent(title.replace(/ /g, RegExp.$1)));
		} else {
			return u.replace(/%title/, encodeURIComponent(title));
		}
	}

	var bar = document.createElement('div');
	bar.style.cssText = 'position:center; text-align:left;';
	bar.innerHTML = html();
	var h1 = document.querySelector('h1');
	h1.style.position = 'relative';
	h1.appendChild(bar);

})();
