// ==UserScript==
// @name         百度网盘使用优化工具 - 网页百度网盘链接状态自动判断
// @namespace    baidu_pan_tool
// @version      1.0.1
// @description  遇到网页中的百度网盘链接再也不需要点击过去查看状态了~，该脚本自动判断哟
// @author       骚气人生
// @icon 		 data:image/png;base64,
// @match        *://*/*
// @grant        GM_xmlhttpRequest
// @grant        GM_getResourceText
// @grant        GM_getValue
// @grant        GM_setValue
// @connect		 pan.baidu.com
// @require      http://libs.baidu.com/jquery/2.0.0/jquery.min.js
// @require 	 https://greasyfork.org/scripts/376401-findandreplacedomtext/code/findAndReplaceDOMText.js?version=660038
// @run-at       document-end
// @compatible	 Chrome
// @compatible	 Firefox
// @compatible	 Edge
// @compatible	 Safari
// @compatible	 Opera
// @compatible	 UC
// ==/UserScript==

(function() {
    'use strict';
	 var $ = $ || window.$;
    var pageTexts = {
		html404Title:"页面不存在",
		htmlNonExistent:"链接不存在",
		htmlNeedPassword:"请输入提取码",
		htmlNormal:"分享无限制",
	}
	var server_window_url = window.location.href;
	
	var validateUrl = {};  //链接状态判断
	var elementObjects = {};
	var	saveKey = [];    //保存对象的key，用shortUrl代替
	var validateUrl = {};
	validateUrl.analysisBaiduPanUrl = function(){
		if (findAndReplaceDOMText(document.body, {
		    find: /(?:https?:\/\/)?pan\.baidu\.com\/s\/([\w\-]{4,25})\b/g,
		    replace: function (e, t) {
		        let pluginNode, createNode, node = e.node,
		            s = t[0],
		            a = !1;
		        for (; node = node.parentNode;) {
		            if ("PANTOOL-PLUGIN" === node.nodeName) {
		                pluginNode = node;
		                break
		            }
		            if ("BODY" === node.nodeName || "HTML" === node.nodeName) break;
		            !a && "A" === node.nodeName && node.href && (a = !0)
		        }
		        if (pluginNode){
		        	createNode = e.text;
		        }else {
		            if (pluginNode = document.createElement("PANTOOL-PLUGIN"), 0 == e.index) {
		                let t = document.createElement("PANTOOL-PLUGIN-TIPS");
		                t.setAttribute("title", ""), pluginNode.appendChild(t);
		                let createNode = document.createTextNode(e.text);
		                pluginNode.appendChild(createNode)
		            } else pluginNode.textContent = e.text;
		            a || (pluginNode.onclick = function (e) {
		                e.stopPropagation();
		                e.preventDefault();		 
		                if(s.indexOf("https") == -1){ s = s.replace("http","https"); }
						if(s.indexOf("https://") == -1){
							s = "https://"+url;
						}
		                window.open(s, "_blank");
		            }), createNode = pluginNode
		        }
		        let c = t[1];
		        return elementObjects[c] ? elementObjects[c].elements.push(pluginNode) : elementObjects[c] = {
		            elements: [pluginNode],
		            pwd: ""
		        }, saveKey.push(c), createNode
		    }, preset: "prose"
		}));
	};
	validateUrl.addCssForPlugin = function(){
		var errorImg = "data:image/png;base64,";
		var successImg = "data:image/png;base64,";
		var lockImg = "data:image/png;base64,";
		var uncertaintyImg = "data:image/png;base64,";		
		var css ="PANTOOL-PLUGIN {display: inline;cursor: pointer;}"+
		"PANTOOL-PLUGIN-TIPS {white-space: nowrap;}"+
		".PANTOOL-PLUGIN-VALIDATE-NOTPASS {text-decoration: line-through;color: #ccc;}"+
		"PANTOOL-PLUGIN-TIPS::before {background-position: center;background-size: 100% 100%;background-repeat: no-repeat;box-sizing: border-box;width: 1em;height: 1em;margin: 0 1px .15em 1px;vertical-align: middle;display: inline-block;}"+
		".PANTOOL-PLUGIN-VALIDATE-NOTPASS>PANTOOL-PLUGIN-TIPS::before {content: '';background-image: url("+errorImg+")}"+
		".PANTOOL-PLUGIN-VALIDATE-PASS>PANTOOL-PLUGIN-TIPS::before {content: '';background-image: url("+successImg+")}"+
		".PANTOOL-PLUGIN-VALIDATE-UNCERTAINTY>PANTOOL-PLUGIN-TIPS::before {content: '';background-image: url("+uncertaintyImg+")}"+
		".PANTOOL-PLUGIN-VALIDATE-LOCK>PANTOOL-PLUGIN-TIPS::before{content: '';background-image: url("+lockImg+")}";
		var $body = document.getElementsByTagName("body")[0];
		var $style = document.createElement("style");
		$style.innerHTML=css;
		$body.appendChild($style);
	};
	//异步方法
	validateUrl.judge = function(url, elements){
		let obj = {"judgeResult":""};
		var baiduXhr = new XMLHttpRequest();
		if(url.indexOf("https") == -1){ url = url.replace("http","https"); }
		if(url.indexOf("https://") == -1){
			url = "https://"+url;
		}
		GM_xmlhttpRequest({
			url: url,
		  	method: "GET",
		  	headers: {"Content-Type": "application/x-www-form-urlencoded"},
		  	onload: function(response) {
				var status = response.status;
				if(status==200||status=='200'){
					var responseText = response.responseText;
					if(!responseText){  //为空，地址出现了重定向 或 其它情况
			   			obj.judgeResult = "isUncertainty";
				   	}else{
				   		responseText = responseText.replace(/\s+/g, "");
					   	if(responseText.length > 5000){
					   		responseText = responseText.substring(0,4999);
					   	}
					   	if(responseText.indexOf(pageTexts.htmlNonExistent)!= -1){
				   			obj.judgeResult = "isNonExistent";  //不存在, 资源被和谐
				   		}
				   		else if(responseText.indexOf(pageTexts.htmlNeedPassword) != -1){
				   			obj.judgeResult = "isNeedPassword"; //需要密码
				   		}
				   		else if(responseText.indexOf(pageTexts.htmlNormal) != -1){
				   			obj.judgeResult = "isOk"; //正常链接
				   		}
				   		else if(responseText.indexOf(pageTexts.html404Title) != -1){
				   			//不确定的链接，可能是访问太快，百度返回链接未找到
				   			obj.judgeResult = "isUncertainty";
				   		}
				   	}
				}else{
					obj.judgeResult = "isNonExistent";
				}
				var $ele;
		        	for(let i = 0; i < elements.length; i++){
		        		$ele = elements[i];
		        		if(obj.judgeResult == "isOk"){ //链接正常
		        			$ele.classList.add("PANTOOL-PLUGIN-VALIDATE-PASS");
		            	}
		        		else if(obj.judgeResult == "isNonExistent"){ //不存在
		            		$ele.classList.add("PANTOOL-PLUGIN-VALIDATE-NOTPASS");
		            	}
		            	else if(obj.judgeResult == "isNeedPassword"){ //需要密码
		            		$ele.classList.add("PANTOOL-PLUGIN-VALIDATE-LOCK");
		            	}
		            	else if(obj.judgeResult == 'isUncertainty'){ //不确定
		            		$ele.classList.add("PANTOOL-PLUGIN-VALIDATE-UNCERTAINTY");
		            	}
		           }
		  	}
		});
	};
	validateUrl.sleep = function(numberMillis) {
		var now = new Date(); 
		var exitTime = now.getTime() + numberMillis; 
		while (true) { 
			now = new Date(); 
			if (now.getTime() > exitTime) {
				return; 
			}
		} 
	};
	validateUrl.createRandom = function(low, high){
		return Math.floor(Math.random()*(high-low)+low);
	};
	validateUrl.collectAndDealUrl = function(){
		let copySaveKey = new Array();
		let isExist = false;
		for (let e = 0; e < saveKey.length; e++) {  //过滤相同key
			isExist = false;
			for(let k=0;k<copySaveKey.length;k++){
				if(saveKey[e] == copySaveKey[k]){
					isExist = true;
					break;
				}
			}
			if(!isExist){
				copySaveKey.push(saveKey[e]);
			}
		}
	    for (let e = 0; e < copySaveKey.length; e++) {
	        let elementObject = elementObjects[copySaveKey[e]];
	        let elements = elementObject.elements;
	        let url = "";
	        for(let i = 0; i < elements.length; i++){
	        	url = url + elements[i].innerText;
	        	if(url.indexOf("pan.baidu.com/s/")!=-1){
	        		break;
	        	}
	        }
	       	if(!!url){
	       		validateUrl.judge(url, elements);
	       	}
	    }
	};
	validateUrl.init=function(){
		validateUrl.addCssForPlugin();
		validateUrl.analysisBaiduPanUrl();
		validateUrl.collectAndDealUrl();
	}
	validateUrl.init();
})();